package com.example.yugimarket;import android.app.Activity;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.android.volley.RequestQueue;import java.util.List;public class RecyclerViewCardAdapter extends RecyclerView.Adapter<CardViewHolder> {    private Activity activity;    private List<CardData> allTheCards;    public RecyclerViewCardAdapter(List<CardData> allTheCards,Activity activity) {        this.allTheCards = allTheCards;        this.activity=activity;    }    @NonNull    @Override    public CardViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.activity_card_view_holder,parent,false);        RecyclerView.LayoutParams layoutParams=new RecyclerView.LayoutParams(ViewGroup.LayoutParams.MATCH_PARENT,ViewGroup.LayoutParams.WRAP_CONTENT);        view.setLayoutParams(layoutParams);        return new CardViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull CardViewHolder holder, int position) {        CardData dataPosition = allTheCards.get(position);        holder.showData(dataPosition,activity);    }    @Override    public int getItemCount(){        return allTheCards.size();    }}